  "requirements": [
    "تحديد الأفكار الرئيسية والتفاصيل الداعمة.",
    "تحديد الملخص بين 5-7 نقاط رئيسية.",
    "استخدام لغة واضحة ورسمية."
  ],
  "output_format": {
    "type": "object",
    "properties": {
      "summary_points": {
        "type": "array",
        "items": {"type": "string"},
        "description": "قائمة نقاط الملخص الرئيسية."
      }
    },
    "required": ["summary_points"]
  },
  "output_example": {
    "summary_points": [
      "يناقش المقال تأثير تغير المناخ.",
      "يبرز الاكتشافات العلمية الحديثة.",
      "يقترح خطوات قابلة للتنفيذ للتخفيف."
    ]
  },
  "resources": ["النص الكامل للمقال", "أدوات تحليل النص"],
  "evaluation_criteria": ["دقة النقاط الرئيسية", "الإيجاز", "الوضوح"],
  "error_handling": ["التعامل مع المقالات غير المكتملة بتلخيص المحتوى المتوفر", "الإشارة إلى المحتوى الغامض للمراجعة"],
  "ethical_considerations": ["ضمان عدم الانتحال", "احترام حقوق النشر"],
  "conclusion": "تحقق من أن جميع النقاط الرئيسية تعكس محتوى المقال بدقة وتلبي متطلبات الطول والوضوح المحددة."
}

