}

examples.content:
{
  "task_description": "خلاصه کردن یک مقاله طولانی به نکات کلیدی مختصر.",
  "requirements": [
    "شناسایی ایده‌های اصلی و جزئیات پشتیبان.",
    "محدود کردن خلاصه به ۵ تا ۷ نکته کلیدی.",
    "استفاده از زبان واضح و رسمی."
  ],
  "output_format": {
    "type": "object",
    "properties": {
      "summary_points": {
        "type": "array",
        "items": {"type": "string"},
        "description": "فهرستی از نکات کلیدی خلاصه."
      }
    },
    "required": ["summary_points"]
  },
  "output_example": {
    "summary_points": [
      "مقاله به تأثیر تغییرات اقلیمی می‌پردازد.",
      "یافته‌های علمی اخیر را برجسته می‌کند.",
      "گام‌های عملی برای کاهش اثرات پیشنهاد می‌دهد."
    ]
  },
  "resources": ["متن کامل مقاله", "ابزارهای تحلیل متن"],
  "evaluation_criteria": ["دقت نکات کلیدی", "اختصار", "وضوح"],
  "error_handling": ["خلاصه کردن محتوای موجود در صورت ناقص بودن مقاله", "علامت‌گذاری محتوای مبهم برای بازبینی"],
  "ethical_considerations": ["اطمینان از عدم سرقت ادبی", "رعایت حقوق نشر"],
  "conclusion": "اطمینان حاصل کنید که همه نکات کلیدی به درستی محتوای مقاله را منعکس کرده و نیازمندی‌های طول و وضوح مشخص شده را برآورده می‌کنند."
}

{
  "task_description": "استخراج اطلاعات تماس مانند نام‌ها، شماره تلفن‌ها و آدرس‌های ایمیل از ایمیل‌ها.",
  "requirements": [
    "شناسایی و استخراج نام کامل.",
    "استخراج شماره تلفن‌های معتبر با فرمت بین‌المللی.",
    "استخراج آدرس‌های ایمیل.",
    "نادیده گرفتن متن نامربوط."
  ],
  "output_format": {
    "type": "object",
    "properties": {
      "contacts": {
        "type": "array",
        "items": {
          "type": "object",
          "properties": {
            "name": {"type": "string"},
            "phone": {"type": "string"},
            "email": {"type": "string"}
          },
          "required": ["name", "email"]
        },
        "description": "فهرست ورودی‌های استخراج شده تماس."
      }
    },
    "required": ["contacts"]
  },
  "output_example": {
    "contacts": [
      {"name": "جان دو", "phone": "+1234567890", "email": "john.doe@example.com"},
      {"name": "جین اسمیت", "phone": "", "email": "jane.smith@example.com"}
    ]
  },
  "resources": ["متن ایمیل‌ها", "عبارات منظم یا ابزارهای پردازش زبان طبیعی"],
  "evaluation_criteria": ["کامل بودن استخراج", "دقت جزئیات تماس", "انطباق با فرمت"],
  "error_handling": ["مدیریت شماره‌های تلفن ناقص به صورت مناسب", "علامت‌گذاری تماس‌های نامشخص یا ناقص برای بازبینی"],
  "ethical_considerations": ["رعایت قوانین حفظ حریم خصوصی و حفاظت داده‌ها"],
  "conclusion": "اطمینان حاصل کنید که همه تماس‌های استخراج شده دقیق، کامل و مطابق با فرمت مشخص شده قبل از نهایی کردن خروجی باشند."
}
